def Fib(M):
    "FASTER - Generates the M th Fibonacci number using a loop"
    a,b = 1,2
    for k in range(M):
        a,b = b, a+b
    return a

# print(Fib(0))

def euler_two(n):
    "when given number n, returns the sum of the first n numbers of the Fibonacci sequence even terms"
    S = 0
    i = 0
    j = 0 #counts only even terms of the Fibonacci sequence
    while (j <= n):
        if Fib(i)%2 == 0:
            S += Fib(i)
            i += 1
            j += 1
        else:
            i += 1
    return S

def mid(t):
    "When given a number t, returns the midpoints (2 digits) of that number"
    T = str(euler_two(t-1))
    midT1 = T[int(len(T)/2 - 1)]
    midT2 = T[int(len(T)/2)]
    if int(len(T))%2 == 0:
        return midT1, midT2
    else:
        return midT2


